import { React, useState } from "react";
import TextField from "@mui/material/TextField";
import "./Search.css";
import Button from "@mui/material/Button";
import tounes from "../images/tounes.png";
import doliprane from "../images/doliprane.png";
import MyNavbar from "./MyNavbar";

export default function Search() {
  const [ref, setRef] = useState("");
  const [product, setProduct] = useState({});

  let inputHandler = (e) => {
    //convert input text to lower case
    var lowerCase = e.target.value.toLowerCase();
    setRef(lowerCase);
  };
  const [clas, setclas] = useState("hidden");
  const [main, setmain] = useState("main");

  const productByRef = () => {
    if (ref.length) {
      const produit = fetch(`http://localhost:5000/api/products/${ref}/product`)
        .then(async (response) => {
          const data = await response.json();
          setProduct(data[0]);
        })
        .catch((error) => console.log(error));
    }
    show();
  };

  const show = () => {
    setclas("shown");
    setmain("hidden");
  };
  return (
    <div>
      <MyNavbar />
      <br />
      <br />
      <br />
      <div className={main}>
        <h2 style={{ color: "white" }}> Reference Of The Medication </h2>
        <div className="search">
          <TextField
            id="outlined-basic"
            className=".bg-light"
            onChange={inputHandler}
            fullWidth
            label="Reference"
            style={{ borderColor: "white", weight: "20px" }}
          />
        </div>
        <Button
          variant="contained"
          style={{ backgroundColor: "#0BE66C" }}
          onClick={() => productByRef()}
        >
          Search
        </Button>
      </div>
      {console.log(product)}
      {product && (
        <div class="container">
          <div class="row m-0">
            <div class="col-lg-7 pb-5 pe-lg-5">
              <div class="row">
                <div class="col-12 p-5">
                  <img src={tounes} alt="" />
                </div>
                <div class="col-12 p-5">
                  <img
                    src={doliprane}
                    style={{ width: "100px", height: "20px" }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
