import * as React from "react";
import Alert from "@mui/material/Alert";
import AlertTitle from "@mui/material/AlertTitle";
import Stack from "@mui/material/Stack";
import swal from "sweetalert";
import Swal from "sweetalert2";
swal({
  title: "Rupture De Doliprane (ref#12655)",
  text: "Le systéme a détecté une rupture dans la region sud ",
  icon: "warning",
  buttons: true,
  dangerMode: true,
}).then((willDelete) => {
  if (willDelete) {
    Swal.fire({
      title: "Qui Contacter ?",
      input: "text",
      inputAttributes: {
        autocapitalize: "off",
      },
      showCancelButton: true,
      confirmButtonText: "Look up",
      showLoaderOnConfirm: true,
      preConfirm: (login) => {
        return fetch(`//api.github.com/users/${login}`)
          .then((response) => {
            if (!response.ok) {
              throw new Error(response.statusText);
            }
            return response.json();
          })
          .catch((error) => {
            Swal.showValidationMessage(`Request failed: ${error}`);
          });
      },
      allowOutsideClick: () => !Swal.isLoading(),
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire({
          title: `${result.value.login}'s avatar`,
          imageUrl: result.value.avatar_url,
        });
      }
    });
  } else {
    swal("Trouver une autre solution ");
  }
});
export default function DescriptionAlerts() {
  return (
    <Stack sx={{ width: "50%" }} spacing={2}>
      <Alert severity="error">
        <AlertTitle>Rupture</AlertTitle>
        Un produit est en rupture de stock —{" "}
        <strong>Voir les détails ! </strong>
      </Alert>
      <Alert severity="warning">
        <AlertTitle>Danger De Rupture</AlertTitle>
        Un produit est en risque de rupture —{" "}
        <strong>Voir les détails !</strong>
      </Alert>
    </Stack>
  );
}
